function showExtraContent(tabNumber) {
const extraText = document.getElementById("extraText");
switch (tabNumber) {
case 1:
extraText.innerHTML = "01/";
extraText.style.marginTop = "0px";
break;
case 2:
extraText.innerHTML = "02/";
extraText.style.marginTop = "50px";
break;
case 3:
extraText.innerHTML = "03/";
extraText.style.marginTop = "100px";
break;
case 4:
extraText.innerHTML = "04/";
extraText.style.marginTop = "150px";
break;
case 5:
extraText.innerHTML = "05/";
extraText.style.marginTop = "200px";
break;
}
}
