function nextStep(step) {
document.querySelectorAll(".stepform").forEach(div => div.classList.add("d-none"));
document.getElementById("step" + step).classList.remove("d-none");
}
function prevStep(step) {
document.querySelectorAll(".stepform").forEach(div => div.classList.add("d-none"));
document.getElementById("step" + step).classList.remove("d-none");
}
function nextEnStep(step) {
document.querySelectorAll(".stepform").forEach(div => div.classList.add("d-none"));
document.getElementById("stepen" + step).classList.remove("d-none");
}
function prevEnStep(step) {
document.querySelectorAll(".stepform").forEach(div => div.classList.add("d-none"));
document.getElementById("stepen" + step).classList.remove("d-none");
}
